ui.UiFactory.layouts.titleLayout = {
    type: "ui.FreeLayout",
    frame: [0, 0, Graphics.width, Graphics.height]
    music: ($ -> $dataFields.database.system.titleMusic),
    preload: { graphics: [folder: ($ -> $dataFields.database.system.titleScreen.folderPath), name: $ -> $dataFields.database.system.titleScreen.name or 'UI/bg-generic.png']},
    controls: [
        {
            "type": "ui.Image",
            "imageHandling": 0,
            "imageFolder":  -> $dataFields.database.system.titleScreen.folderPath || "Graphics/Pictures",
            "image": -> $dataFields.database.system.titleScreen.name or 'UI/bg-generic.png',
            "frame": [0, 0, Graphics.width, Graphics.height]
        }
    ,
        {
            type: "ui.Text",
            id: "newGameText",
            #initialFocus: true,
            #focusable: true,
            #nextKeyObject: "loadGame",
            styles: ["titleText"],
            text: { 
                lcId: "4FE8B0BD58FD914BFA8824E07226FB7F7974", 
                defaultText: "New Game" 
            }
            frame: ["70%", "50%"],
            sizeToFit: true,
            action: {
                name: "newGame"
            }
        }
    ]
}

